package com.weishi.yiye.bean;import android.text.TextUtils;import java.util.Map;/***************************** * @Copyright(c) 2014-2018 * 长沙壹晟众美网络科技有限公司 All Rights Reserved. * @Author：yezhouyong * @Date：2018/2/1 * @Description：处理支付宝支付结果的实体类 * @Version:v1.0.0 *****************************/public class PayResultBean {    public String resultStatus;    public String result;    public String memo;    public PayResultBean(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            }        }    }    @Override    public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}";    }    /**     * @return the resultStatus     */    public String getResultStatus() {        return resultStatus;    }    /**     * @return the memo     */    public String getMemo() {        return memo;    }    /**     * @return the result     */    public String getResult() {        return result;    }}